import cv2import numpy as npdef detect_obstruction(frame):    #定义检测事件函数    gray = cv2.cvtColor(frame, cv2.COLOR_BGR2GRAY)    blur = cv2.GaussianBlur(gray, (5,5), 0)    thresh = cv2.threshold(blur, 60, 255, cv2.THRESH_BINARY_INV)[1]    contours, hierarchy = cv2.findContours(thresh, cv2.RETR_EXTERNAL, cv2.CHAIN_APPROX_SIMPLE)    if contours:        #检测到遮挡事件样式        cv2.putText(frame, 'Obstruction Detected', (10, 50), cv2.FONT_HERSHEY_TRIPLEX, 1, (0, 255, 0), 2)    return frame# 读取图像frame = cv2.imread(r'C:\Users\zhangzuo\Pictures\20230606135741.png')frame = detect_obstruction(frame)cv2.imshow("Detection", frame)cv2.waitKey(0)  # 0 表示无限等待，其他数值表示等待毫秒数cv2.destroyAllWindows()  # 关闭所有窗口