import cv2image = cv2.imread(r'C:\Users\zhangzuo\Pictures\zhedang18.png', cv2.IMREAD_GRAYSCALE)ret, thresholded = cv2.threshold(image, 127, 255, 0)contours, _ = cv2.findContours(thresholded, cv2.RETR_EXTERNAL, cv2.CHAIN_APPROX_SIMPLE)total_num_points = 0for contour in contours:    num_points = len(contour)    # print(f"Contour has {num_points} boundary points")    total_num_points+=len(contour)print(total_num_points)# cv2.imshow('Contours', image)cv2.waitKey(0)cv2.destroyAllWindows()